import { assertEquals } from "jsr:@std/assert@^0.223.0";
import { normalize } from "./normalize.ts";

Deno.test(`normalize() returns "." if input is empty`, function () {
  assertEquals(normalize(""), ".");

  const pwd = Deno.cwd();
  assertEquals(normalize(pwd), pwd);
});
